import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import Moment from "moment";
import firebase from "../../Firebase";
import useStyles from "./JoinRoom.styles";
import {
  Typography,
  TextField,
  Container,
  Button,
  Toolbar,
  AppBar,
  IconButton,
  Paper
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import rr from "../../img/rr.jpg";
import csk from "../../img/csk.jpg";
import rcb from "../../img/rcb.jpg";
import mi from "../../img/mi.jpg";
import kxip from "../../img/kxip.jpg";
import dc from "../../img/dc.jpg";
import kkr from "../../img/kkr.jpg";
import ipl from "../../img/ipl.png"

function JoinRoom() {
  const classes = useStyles();
  const [myRoom, setMyRoom] = useState([]);
  const [showLoading, setShowLoading] = useState(true);
  const [nickName, setNickName] = useState("");
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      setNickName(localStorage.getItem("nickName"));
      firebase
        .database()
        .ref("myRooms/")
        .on("value", (resp) => {
          setMyRoom([]);
          setMyRoom(snapshotToArray(resp));
          setShowLoading(false);
        });
    };

    fetchData();
  }, []);

  const snapshotToArray = (snapshot) => {
    const returnArr = [];

    snapshot.forEach((childSnapshot) => {
      const item = childSnapshot.val();
      item.key = childSnapshot.key;
      returnArr.push(item);
    });

    return returnArr;
  };

  const enterChatmyRoom = (myRoomname) => {
    const chat = {
      myRoomname: "",
      nickName: "",
      message: "",
      date: "",
      type: "",
    };
    chat.myRoomname = myRoomname;
    chat.nickName = nickName;
    chat.date = Moment(new Date()).format("DD/MM/YYYY HH:mm:ss");
    chat.message = `${nickName} enter the myRoom`;
    chat.type = "join";
    const newMessage = firebase.database().ref("chats/").push();
    newMessage.set(chat);

    firebase
      .database()
      .ref("myRoomusers/")
      .orderByChild("myRoomname")
      .equalTo(myRoomname)
      .on("value", (resp) => {
        let myRoomuser = [];
        myRoomuser = snapshotToArray(resp);
        const user = myRoomuser.find((x) => x.nickName === nickName);
        if (user !== undefined) {
          const userRef = firebase.database().ref("myRoomusers/" + user.key);
          userRef.update({ status: "online" });
        } else {
          const newmyRoomuser = { myRoomname: "", nickname: "", status: "" };
          newmyRoomuser.myRoomname = myRoomname;
          newmyRoomuser.nickName = nickName;
          newmyRoomuser.status = "online";
          const newmyRoomUser = firebase.database().ref("myRoomusers/").push();
          newmyRoomUser.set(newmyRoomuser);
        }
      });

    history.push("/chatmyRoom/" + myRoomname);
  };

  const logout = () => {
    localStorage.removeItem("nickName");
    history.push("/login");
  };

  return (
    <div className={classes.mainContainer}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="inherit"
            aria-label="menu"
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            Welcome to IPL Auction
          </Typography>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
      <Container component="main" maxWidth="xs">
        <Paper className={classes.innerContainer} square>
        <img src={ipl} className={classes.iplLogo}/>
          <TextField
            label="Room"
            variant="outlined"
            placeholder="Room"
            fullWidth
            required={true}
            InputLabelProps={{ shrink: true }}
            value={myRoom}
            onChange={(e) => {
              setMyRoom(e.target.value);
            }}
          />

          <Button color="primary" variant="contained" fullWidth className={classes.submitButton}>
            Join
          </Button>
          </Paper>
      </Container>
      <div className={classes.imgContainer}>
        <img src={rr} className={classes.teamImg} />
        <img src={csk} className={classes.teamImg} />
        <img src={rcb} className={classes.teamImg} />
        <img src={mi} className={classes.teamImg} />
        <img src={dc} className={classes.teamImg} />
        <img src={kxip} className={classes.teamImg} />
        <img src={kkr} className={classes.teamImg} />
      </div>
    </div>
  );
}

export default JoinRoom;

