import React, { useState } from 'react';
import { useHistory } from "react-router-dom";
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import { Typography, TextField, Container, IconButton } from "@material-ui/core";
import firebase from '../../Firebase';

function Login() {
    const classes = useStyles();
    const history = useHistory();
    const [user, setUser] = useState({
        nickName: "",
      });
    const ref = firebase.database().ref('users/');

    const login = (e) => {
        e.preventDefault();
        setShowLoading(true);
        ref.orderByChild('nickname').equalTo(user.nickName).once('value', snapshot => {
            if (snapshot.exists()) {
                localStorage.setItem('nickName', creds.nickname);
                history.push('/roomlist');
                setShowLoading(false);
            } else {
                const newUser = firebase.database().ref('users/').push();
                newUser.set(creds);
                localStorage.setItem('nickName', creds.nickname);
                history.push('/roomlist');
                setShowLoading(false);
            }
        });
    };

    const onChange = (e) => {
        e.persist();
        setCreds({...creds, [e.target.name]: e.target.value});
    }

    return (
        <div className={classes.mainContainer}>
      <Container component="main" maxWidth="xs">
        <div className={classes.innerContainer}>
          <Typography variant="h3">Join</Typography>
          <TextField
            label="Name"
            variant="outlined"
            fullWidth
            required={true}
            InputLabelProps={{ shrink: true }}
            value={user.nickName}
            onChange={(e) => {
                setUser({
                ...user,
                nickName: e.target.value,
              });
            }}
          />

          <IconButton
            className={classes.submitButton}
            color="primary"
            onClick={handleSubmit}
          >
            <ArrowForwardIcon fontSize = "large"/>
          </IconButton>
        </div>
      </Container>
    </div>
    );
}

export default Login;
