import { makeStyles } from "@material-ui/core";

export default makeStyles((theme) => ({
  mainContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "center",
    flexGrow: 1,
    backgroundImage: 'radial-gradient(circle at -40% -10%,#4f76f2,#0f1b41 60%)',
    backgroundSize: "cover",
    backgroundRepeatL
  },
  innerContainer: {
    marginTop: theme.spacing(10),
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
    },
    flexWrap: "wrap",
    justifyContent: "center",
    width: "27vw",
  },
  submitButton: {

  },
}));
