import { makeStyles } from "@material-ui/core";

export default makeStyles((theme) => ({
  mainContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "center",
  },
  innerContainer: {
    marginTop: theme.spacing(10),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    flexWrap: "wrap",
    justifyContent: "center",
    width: "27vw",
    height: "30vh",
    padding: "20px",
    margin: "100px 0",
    borderRadius: "10px"
  },
  doraImg:{
      height: "210px"
  }
}));
